import React, { useState } from "react";

function EditMechanic({ id, body, onUpdateMechanic }){
  const [mechDetails, setMechDetails] = useState(body)

  function handleFormSubmit(e){
    e.preventDefault()



    fetch(`http://localhost:9292/mechanics/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type":"application/json"
      },
      body: JSON.stringify({
        body: mechDetails
      })
    })
    .then((resp) => resp.json())
    .then((updatedReview) => onUpdateMechanic(updatedReview))
  }


  return (
    <div>
      <form className="" onSubmit={handleFormSubmit}>
        <input 
          type="text"
          name="body"
          value={mechDetails}
          onChange={(e) => setMechDetails(e.target.value)}
        />
        <input type="submit" value="Save Details"/>
      </form>
    </div>
  )
}
export default EditMechanic;
