import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";



export default function MechanicDetails(){

  let {mechId} = useParams()
  const DISPLAY_MECHANICS = `http://localhost:9292/mechanics/${mechId}`

  const [image_url, setImage] = useState("")
  const [shop_name, setShopName] = useState("")
  const [number, setNumber] = useState("")
  const [mechanic_name, setMechName] = useState("")
  const [email, setEmail] = useState("")
  const [about, setAbout] = useState("")
  const [experience, setExperience] = useState("")
  const [rating, setRating] = useState("")
  const [category, setCategory] = useState("")
  const [comment, setComment] = useState("")
  const [name, setName] = useState("")
  const [time, setTime] = useState("")
  const [review, setReview] = useState([])
  const [totalRating, setTotalRating] = useState("")


  useEffect(() => {
    fetch(DISPLAY_MECHANICS)
    .then((resp) => resp.json())
    .then((data) => {

      const mechanic = data

      setImage(mechanic.image_url)
      setShopName(mechanic.shop_name)
      setAbout(mechanic.about)
      setExperience(mechanic.experience)
      setEmail(mechanic.email)
      setMechName(mechanic.mechanic_name)
      setNumber(mechanic.number)
      setCategory(mechanic.category)
      setReview(mechanic.reviews)
      setTotalRating((mechanic.reviews).reduce((a, b) => {
        return a + b.rating
      }, 0))

    })
  }, [])

  function handleAddReview(e){

    e.preventDefault()

    const newReview = {
      name: name,
      comment: comment,
      rating: rating
    }


    fetch("http://localhost:9292/mechanics", {
      method: "POST",
      headers: {"Content-Type":"application/json"},
      body: JSON.stringify(newReview)
    })
    .then((resp) => resp.json())
    .then((data) => console.log(data))
  }


  return(
    <div>
      <center className="container" style={{marginTop: 10+"vh", marginBottom: 10+"vh"}} >
        <h2 className="shop-name">{shop_name}</h2>
        <div className="card" style={{width: 40+"rem"}}>
          <img src={image_url} className="card-img-top" alt={mechanic_name}/>
          <div className="card-body">
            <h5 className="card-title">{mechanic_name}</h5>
            <span className="badge text-bg-danger">{category}</span>
            <p className="card-text" style={{minHeight: 35+"vh"}}>{about}</p>
          </div>
          <ul className="list-group list-group-flush text-bg-info">
            <li className="list-group-item badge text-bg-info">Tel: {number}</li>
            <li className="list-group-item badge text-bg-info">Email: {email}</li>
            <li className="list-group-item badge text-bg-info">Experience: {experience} years</li>
            <li className="list-group-item badge text-bg-info">Rating: {(totalRating/rating.length).toFixed(1)} / 5</li>
          </ul>

          <h5 className="card-title">Reviews</h5>
          {review.map((review1) => {
            return (
              <div className="card" style={{width: 18+"rem"}}>
                <div className="card-body" key={review1.id}>
                  <h6 className="card-subtitle mb-2 text-muted" style={{color: "red"}}>{review1.name}</h6>
                  <p className="card-text" style={{color: "red"}}>Rating: {review1.rating} <span>{review1.update_at}</span></p>
                  <p className="card-text">Comment: {review1.comment}</p>

                  <button  className="card-link">Edit Review</button>
                </div>
              </div>
            )
          })}

          <form onSubmit={handleAddReview}>
          <select className="form-select form-select-sm" onChange={(e) => setRating(e.target.value)}>
              <option>Rating</option>
              <option value="1">1</option>
              <option value="2">1</option>
              <option value="3">1</option>
              <option value="4">1</option>
              <option value="5">1</option>
            </select>
            <input type="text" className="form-control" placeholder="4" onChange={(e) => setName(e.target.value)}/>
            <input type="text" className="form-control" placeholder="4" onChange={(e) => setComment(e.target.value)}/>


            <button type="submit">Add Review</button>
          </form>
        </div>
      </center>
    </div>
  )
}
