import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";


const DISPLAY_MECHANICS = "http://localhost:2000/mechanics"

export default function MechanicDetails(){

  const [image, setImage] = useState("")
  const [shopName, setShopName] = useState("")
  const [number, setNumber] = useState("")
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [about, setAbout] = useState("")
  const [experience, setExperience] = useState("")
  const [rating, setRating] = useState("")

  let {mechId} = useParams()
  const actualSearch = DISPLAY_MECHANICS + mechId

  useEffect(() => {
    fetch(DISPLAY_MECHANICS)
    .then((resp) => resp.json())
    .then((data) => {
      const mechanic = data[0]
      console.log(mechanic)

      setImage(mechanic.photo)
      setShopName(mechanic.shopName)
      setAbout(mechanic.about)
      setRating(mechanic.rating)
      setExperience(mechanic.experience)
      setEmail(mechanic.contactInfo.email)
      setName(mechanic.contactInfo.name)
      setNumber(mechanic.contactInfo.phone)

    })
  }, []) 

  return(
    <div>
      <center>
      <h2>{shopName}</h2>
      <div className="card" style={{width: 25+"rem"}}>
        <img src={image} className="card-img-top" alt={name}/>
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
        <p className="card-text">{about}</p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">{number}</li>
          <li className="list-group-item">A second item</li>
          <li className="list-group-item">A third item</li>
        </ul>
        <div className="card-body">
          <a href="#" className="card-link">Card link</a>
          <a href="#" className="card-link">Another link</a>
        </div>
      </div>
        <img style={{height: 300+"px", width: 300+"px"}} src={image} alt={name}/>
        <div>
          <h5>{name}</h5>
          <h6>{number}</h6>
          <h6>{email}</h6>
        </div>
        <p>{about}</p>
        <span className="badge rounded-pill text-bg-info">Experience: {experience} years</span> <br></br>
        <span className="badge rounded-pill text-bg-info">Rating {rating} / 5</span> 

      </center>
    </div>
  )
}
