import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";


const DISPLAY_MECHANICS = "http://localhost:2000/mechanics"

export default function MechanicDetails(){

  const [image, setImage] = useState("")
  const [shopName, setShopName] = useState("")
  const [number, setNumber] = useState("")
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [about, setAbout] = useState("")
  const [experience, setPrice] = useState("")
  const [rating, setRating] = useState("")

  let {mechId} = useParams()
  const actualSearch = DISPLAY_MECHANICS + mechId

  useEffect(() => {
    fetch(actualSearch)
    .then((resp) => resp.json())
    .then((data) => {
      const mechanic = data.mechanics
      console.log(mechan)

    })
  }, []) 

  return(
    <div>
      <h2>Mechanic Details</h2>
      <center>
        <img style={{height: 300+"px", width: 300+"px"}} src={image} alt={name}/>
        <h2>{shopName}</h2>
        <div>
          <h5>{name}</h5>
          <h5>{number}</h5>
          <h5>{email}</h5>
        </div>
        <p>{about}</p>
        <span class="badge rounded-pill text-bg-info">
        {experience} {rating}
        </span> 
      </center>
    </div>
  )
}
