import React, { useState } from "react"

function AddReview({onAddReview}){
  const [comment, setComment] = useState("")
  const [name, setName] = useState("")
  const [time, setTime] = useState("")
  const [rating, setRating] = useState([])
  const [id, setId] = useState("")



  function handleAddReview(e){
    e.preventDefault()

    const newReview = {
      mechanic_id: id,
      name: name,
      comment: comment,
      rating: rating

    }

    fetch(`http://localhost:9292/reviews`, {
      method: "POST",
      headers: {"Content-Type":"application/json"},
      body: JSON.stringify(newReview)
    })
    .then((resp) => resp.json())
    .then((data) => onAddReview(data))
  }

  return (
    <div>
          <h5 className="card-title">Reviews</h5>
          {review.map((review) => {
            return (
              <div className="card" style={{width: 75+"%", margin: "auto"}}  key={review.id}>
                <div className="card-body" >
                  <h6 className="card-subtitle mb-2 text-muted" style={{color: "red"}}>{review.name}</h6>
                  <p className="card-text" style={{color: "red"}}>Rating: {review.rating} <span>{review.update_at}</span></p>
                  <p className="card-text">Comment: {review.comment}</p>

                  <button  className="card-link">Edit Review</button>
                </div>
              </div>
            )
          })}

          <form onSubmit={handleAddReview}>
          <input type="text" className="form-control" placeholder="Enter name" onChange={(e) => setName(e.target.value)}/>
          <select className="form-select form-select-sm" onChange={(e) => setRating(e.target.value)}>
              <option>Rating</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
            <textarea type="textArea" className="form-control" rows="3" placeholder="comment" onChange={(e) => setComment(e.target.value)}></textarea>
            <button type="submit">Add Review</button>
          </form>
    </div>
  )
}
export default AddReview
